Get-ADComputer -Filter { OperatingSystem -NotLike '*Windows Server*'} -SearchBase 'DC=test,DC=test,DC=edu' -SearchScope Subtree -Server test.test.edu -Properties OperatingSystem,OperatingSystemVersion,lastLogonDate | where { $_.lastLogonDate -lt (get-date).addmonths(-6) } | Select Name,OperatingSystem,OperatingSystemVersion,lastLogonDate| Sort LastLogonDate | Export-CSV C:\users\test\Desktop\AD_Computer_Report.csv -NoTypeInformation -Encoding UTF8